
public class OperatorReview {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 82;
		System.out.println("num1%9 is : "+num1%9);// print 1;
		System.out.println("num1/9 is : "+num1/9);// print 9
		
		double num2 = 82.9999;
		System.out.println("num2%9 is : "+num2%9);
		System.out.println("num2/9 is : "+num2/9);
		
		int i = 0;
		int j = 0;
		System.out.println("i++ is : "+ i++);
		System.out.println("++j is : "+ j++);
	}

}
